   - Create resource: POST /api/v1/namespaces/{namespace}/resources
   - Get resource: GET /api/v1/namespaces/{namespace}/resources/{name}
   - Update resource: PUT /api/v1/namespaces/{namespace}/resources/{name}
   - Delete resource: DELETE /api/v1/namespaces/{namespace}/resources/{name}

